import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';
import {Customer} from './customer'; 

@Injectable({
  providedIn: 'root'
})
export class CustomerService {

  private url = 'https://getinvoices.azurewebsites.net/api';

  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };

  constructor(private http: HttpClient) { }

  getCustomers(): Observable<Customer[]> {
    //return this.http.get<Customer[]>(baseUrl + 'Customers').subscribe(result => {
    //  this.customers = result;
    //}, error => console.error(error));
    return this.http.get<Customer[]>(`${this.url}/Customers`);
  }

  getCustomer(id: string): Observable<Customer> {
    return this.http.get<Customer>(`${this.url}/Customer/${id}`);
  }

  deleteCustomer(id: string): Observable<Customer> {
    return this.http.delete<Customer>(`${this.url}/Customer/${id}`, this.httpOptions).pipe();
  }
}
