import { Component } from '@angular/core';
import { ActivatedRoute, ParamMap } from '@angular/router';
import { Customer } from '../customer';
import { CustomerService } from '../customer.service';

@Component({
  selector: 'app-customer-detail',
  templateUrl: './customer-detail.component.html',
  styleUrls: ['./customer-detail.component.css']
})
export class CustomerDetailComponent {
  customer: Customer | undefined;
  constructor(
    private route: ActivatedRoute,
    private customerService: CustomerService
  ) { }

  ngOnInit(): void {
    this.getCustomer();
    //console.log(JSON.stringify(this.customer));
  }
  goBack(): void { }
  save(): void { }
  getCustomer(): void {
    const id = this.route.snapshot.paramMap.get('customerId');
    console.log("ID->" + id);
    if (id != null) {
    this.customerService.getCustomer(id)
      .subscribe({
        next: data => {
          this.customer = data;
          console.log(JSON.stringify(this.customer));
        },
        error: error => {
          console.error('There was an error!', error);
        }
      });
    }
  }
}
  <h2>{{customer.firstname | uppercase}} Details</h2>
  <div><span>id: </span>{{customer.id}}</div>
  <div>
    <label for="customer-firstname">Customer name: </label>
    <input id="customer-firstname" [(ngModel)]="customer.firstname" placeholder="Customer firstname" />
  </div>
  <button class="btn btn-primary" type="button" (click)="goBack()">go back</button>
  <button class="btn btn-primary" type="button" (click)="save()">save</button>
