import { Component } from '@angular/core';
import { FormBuilder, Validators, FormControl } from '@angular/forms';
import { Customer } from '../customer';
import { CustomerService } from '../customer.service';
import { Location } from '@angular/common';

@Component({
  selector: 'app-add-customer',
  templateUrl: './add-customer.component.html',
  styleUrls: ['./add-customer.component.css']
})
export class AddCustomerComponent {
  nextId: number = 0;
  customer: Customer | undefined;
  customerForm = this.formBuilder.group({
    firstname: '',
    lastname: '',
    email: new FormControl("", [Validators.required, Validators.email]),
    phone_Number: '',
    country_code: '',
    gender: '',
    balance: 0,
  });
  constructor(
    private formBuilder: FormBuilder,
    private customerService: CustomerService,
    private location: Location
  ) {
    this.customerService.getCustomers()
      .subscribe({
        next: data => {
          this.nextId = Math.max(...data.map(({ id }) => Number(id))) + 1;
        },
        error: error => {
          console.error('There was an error!', error);
        }
      });
  }

  add(): void {
    let firstname = this.customerForm.value.firstname;
    let lastname = this.customerForm.value.lastname;
    let email = this.customerForm.value.email;
    let phone_Number = this.customerForm.value.phone_Number;
    let country_code= this.customerForm.value.country_code;
    let gender = this.customerForm.value.gender;
    let balance = this.customerForm.value.balance;

    let rowKey = `${this.nextId}`;

    this.customerService.addCustomer({ firstname, lastname, email, phone_Number, country_code, gender, balance, rowKey } as Customer)
      .subscribe(() => this.goBack());
  }

  goBack(): void { this.location.back(); }

  protected get registerFormControl() {
    return this.customerForm.controls;
  }
}
<h2>Add New Customer</h2>
<br />
<form [formGroup]="customerForm" (ngSubmit)="add()" autocomplete="off">

  <div class="form-group">
    <label for="firstname">
      Firstname
    </label>
    <input id="firstname" class="form-control" type="text" formControlName="firstname">

  </div>

  <div>
    <label for="lastname">
      Lastname
    </label>
    <input id="lastname" class="form-control" type="text" formControlName="lastname">
  </div>

  <div>
    <label for="email">
      Emaill
    </label>
    <input id="email" class="form-control" type="text" formControlName="email">

  </div>

  <div>
    <label for="phone_Number">
      Phone Number
    </label>
    <input id="phone_Number" class="form-control" type="text" formControlName="phone_Number">
  </div>

  <div>
    <label for="country_code">
      Country Code
    </label>
    <input id="country_code" class="form-control" type="text" formControlName="country_code">
  </div>

  <div>
    <label for="gender">
      Gender
    </label>
    <input id="gender" class="form-control" type="text" formControlName="gender">
  </div>

  <div>
    <label for="balance">
      Balance
    </label>
    <input id="balance" class="form-control" type="text" formControlName="balance">
  </div>

  <br />
  <button class="btn btn-primary" type="submit">Add</button>

</form>
